for FILE in ./examples/testdata/dos_line_endings ./examples/testdata/multiple_unis ./examples/testdata/partial_frames ./examples/testdata/single_uni ./examples/testdata/trailing_timeout; do echo "Checking $FILE"; ./examples/ola_recorder --verify $FILE; STATUS=$?; if [ $STATUS -ne 0 ]; then echo "FAIL: $FILE caused ola_recorder to exit with status $STATUS"; exit $STATUS; fi; done; exit 0
