for FILE in ./tools/ola_trigger/example.conf ./tools/ola_trigger/test_file.conf ./tools/ola_trigger/test_file_falling.conf ./tools/ola_trigger/test_file_rising.conf ./tools/ola_trigger/contrib/crelay.conf ./tools/ola_trigger/contrib/mac_volume.conf ./tools/ola_trigger/contrib/mac_itunes.conf ./tools/ola_trigger/contrib/philips_hue_osram_lightify.conf; do echo "Checking $FILE"; ./tools/ola_trigger/ola_trigger --validate $FILE; STATUS=$?; if [ $STATUS -ne 0 ]; then echo "FAIL: $FILE caused ola_trigger to exit with status $STATUS"; exit $STATUS; fi; done; exit 0
